package com.itssvkv.todolist.repository.firebaseimport com.google.firebase.auth.FirebaseAuthimport com.google.firebase.firestore.FirebaseFirestoreimport javax.inject.Injectclass FirebaseRepositoryImpl@Inject constructor(    private val auth: FirebaseAuth,    private val db: FirebaseFirestore) : FirebaseRepository {    override fun currentUserId() =        auth.uid    override suspend fun currentUserDetails() =        db.collection("users").document(currentUserId()!!)}