package com.itssvkv.todolist.ui.home.pagerimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.itssvkv.todolist.ui.homescreen.HomeFragmentimport com.itssvkv.todolist.ui.profile.ProfileFragmentclass PagerAdapter (fragment: Fragment) : FragmentStateAdapter(fragment) {    override fun getItemCount(): Int = 2    override fun createFragment(position: Int): Fragment {        when (position) {            0 -> return HomeFragment()            1 -> return ProfileFragment()        }        return HomeFragment()    }}