package com.itssvkv.chatapp.ui.onboarding.pagerimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.itssvkv.todolist.ui.onboarding.pager.FirstFragmentimport com.itssvkv.todolist.ui.onboarding.pager.SecondFragmentimport com.itssvkv.todolist.ui.onboarding.pager.ThirdFragmentclass PagerAdapter(fragment: Fragment) : FragmentStateAdapter(fragment) {    override fun getItemCount(): Int = 3    override fun createFragment(position: Int): Fragment {        when (position) {            0 -> return FirstFragment()            1 ->                return SecondFragment()            2 -> return ThirdFragment()        }        return FirstFragment()    }}